import {Add, PlayArrow, ThumbDownOutlined, ThumbUpOutlined} from "@material-ui/icons";
import "./listItem.scss";
import {useState} from "react";

export default function ListItem({index}) {
    const [isHovered, setIsHovered] = useState(false);
    const trailer = "https://www.youtube.com/watch?v=0BI69yrlvnI";
    return (
        <div
            className="listItem"
            style={{left: isHovered && index * 225 - 50 + index * 2.5}}
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}>
            <img src="Akalist.jpg" alt="" />
            <video src={trailer} autoPlay={true} loop />
            <div className="itemInfo">
                <div className="icons">
                    <PlayArrow />
                    <Add />
                    <ThumbUpOutlined />
                    <ThumbDownOutlined />
                </div>
                <div className="itemInfoTop">
                    <span>1 hour 10 mins</span>
                    <span className="limit">+16</span>
                    <span>2023</span>
                </div>
                <div className="decs">
                    Lorem ipsum dolor sit amet consectetur adipisicing elit. Minus tempore hic nobis perferendis velit labore sequi, exercitationem delectus
                    qui, quod porro minima eos dolorum, repellat ad inventore dolore modi. Officia.
                </div>
                <div className="genre">Action</div>
            </div>
        </div>
    );
}
